  OP_REQUIRES_OK_ASYNC(
      c,
      FillCollectiveParams(col_params, group_assignment, REDUCTION_COLLECTIVE,
                           resource.get()),
      done);
  col_params->instance.shape = c->input(0).shape();
  col_params->merge_op = merge_op_.get();

