    if (m_nPos >= m_nLen)
        nSize = 0;
    else if (nSize > (m_nLen - m_nPos))
        nSize = m_nLen - m_nPos;

    memcpy( pDestBuffer, (m_sFile + m_nPos), nSize );
    m_nPos += nSize;

    return nSize;
}

