  if (wcb_) {
    wcb_->setSocket(socket_);
  }

  // Write back the same data.
  socket_->write(wcb_, currentBuffer.buffer, len, writeFlags);

  buffers.push_back(currentBuffer);
  currentBuffer.reset();
  state = STATE_SUCCEEDED;
}

